const Schema = require("./schema.js");
/**
 * @template
 * An SQL schema fragment for tables which have an auto incrementing primary id key.
 * */
var AutoIdSchema =  new Schema({
  id:["int",["primary key",'auto_increment']]
},
{
  remove(id) {
    return this.table.delete({id:id});
  },
  get(id) {
    return this.table.selectWhere();
  }
});
module.exports = AutoIdSchema;
